import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
  amount!:number;
  fromCurrency:string ='USD';
  toCurrency:string = 'USD';
  resValue:any;
  submitButton(fromCurrency:any,toCurrency:any)
  {
    if(fromCurrency == 'INR' && toCurrency=='USD')
    {
      this.resValue=1.00.toFixed(2);
      return this.resValue;
    }
    else if(fromCurrency == 'USD' && toCurrency=='GBP')
    {
      this.resValue=((0.876893/1.126735)*this.amount).toFixed(2);
      return this.resValue;
    }
    else if(fromCurrency == 'GBP' && toCurrency=='USD')
    {
      this.resValue=((1.126735/0.876893)*this.amount).toFixed(2);
      return this.resValue;
    }
    else
    {
      return "invaild";
    }
  }
}
<
<form>
<h1>Currency Converter</h1>
<input type="text" [(ngModel)]="amount" placeholder="Enter Amount"/>
<select [(ngModel)]="fromCurrency">
  <option value="USD">USD</option>
  <option value="INR">INR</option>
  <option value="GBP">GBP</option>
</select>
<select [(ngModel)]="toCurrency">
  <option value="USD">USD</option>
  <option value="INR">INR</option>
  <option value="GBP">GBP</option>
</select>
<button (click)="submitButton(fromCurrency,toCurrency)">submit</button>
<p>
<span>Converted : {{resValue }} </span>
</p>
</form>
