import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
  fromCurrency=0;
  toCurrency=0;
   amount=0;
   result:any;
   setSource(s:any){
     this.fromCurrency=s;
   } 

   setTarget(t:any){
     this.toCurrency=t;

   }
   convert()
   {
    this.result=(this.toCurrency/this.fromCurrency)*this.amount;
    this.result = Math.round(this.result);
   }
   reset(){
    this.amount=0;
    this.result=0;
    this.toCurrency=0;
    this.fromCurrency=0;
  }
 }
